build-for-vercel.js.content:
#!/usr/bin/env node

/**
 * Build script for deploying à¦¦à§‹à¦•à¦¾à¦¨ à¦¹à¦¿à¦¸à¦¾à¦¬ to Vercel
 * This script prepares the React app for production deployment
 */

import { execSync } from 'child_process';
import fs from 'fs';
import path from 'path';

console.log('ğŸ—ï¸  Building à¦¦à§‹à¦•à¦¾à¦¨ à¦¹à¦¿à¦¸à¦¾à¦¬ for Vercel deployment...\n');

try {
  // Step 1: Clean previous builds
  console.log('1ï¸âƒ£  Cleaning previous builds...');
  if (fs.existsSync('dist')) {
    execSync('rm -rf dist', { stdio: 'inherit' });
  }
  if (fs.existsSync('client/dist')) {
    execSync('rm -rf client/dist', { stdio: 'inherit' });
  }

  // Step 2: Install dependencies
  console.log('2ï¸âƒ£  Installing dependencies...');
  execSync('npm install', { stdio: 'inherit' });

  // Step 3: Build React app
  console.log('3ï¸âƒ£  Building React application...');
  execSync('npm run build', { stdio: 'inherit' });

  // Step 4: Verify build output
  console.log('4ï¸âƒ£  Verifying build output...');
  const distPath = 'dist/public'; // Vite outputs to dist/public based on vite.config.ts

  if (!fs.existsSync(distPath)) {
    throw new Error('Build output directory not found at dist/public');
  }

  const buildFiles = fs.readdirSync(distPath);
  console.log(`   âœ… Build successful! Generated ${buildFiles.length} files in ${distPath}/`);

  // Step 5: Create vercel.json if it doesn't exist
  console.log('5ï¸âƒ£  Configuring Vercel settings...');
  const vercelConfig = {
    "name": "dokan-hisab",
    "version": 2,
    "builds": [
      {
        "src": "package.json",
        "use": "@vercel/static-build",
        "config": {
          "distDir": distPath
        }
      }
    ],
    "routes": [
      {
        "src": "/(.*)",
        "dest": "/index.html"
      }
    ],
    "env": {
      "NODE_ENV": "production"
    }
  };

  if (!fs.existsSync('vercel.json')) {
    fs.writeFileSync('vercel.json', JSON.stringify(vercelConfig, null, 2));
    console.log('   âœ… Created vercel.json configuration');
  }

  // Step 6: Display deployment instructions
  console.log('\nğŸš€ Build Complete! Ready for Vercel deployment\n');
  console.log('ğŸ“‹ Deployment Options:');
  console.log('');
  console.log('   Option 1 - Vercel CLI:');
  console.log('   npm install -g vercel');
  console.log('   vercel --prod');
  console.log('');
  console.log('   Option 2 - Vercel Dashboard:');
  console.log('   1. Push to GitHub');
  console.log('   2. Connect repository at vercel.com');
  console.log('   3. Deploy automatically');
  console.log('');
  console.log('   Option 3 - Manual Upload:');
  console.log(`   1. Upload ${distPath}/ folder to any web host`);
  console.log('   2. Configure as static site');
  console.log('');
  console.log('ğŸ‰ Your à¦¦à§‹à¦•à¦¾à¦¨ à¦¹à¦¿à¦¸à¦¾à¦¬ app will be live and accessible worldwide!');

} catch (error) {
  console.error('\nâŒ Build failed:', error.message);
  console.error('\nğŸ”§ Troubleshooting:');
  console.error('   1. Ensure Node.js is installed');
  console.error('   2. Run: npm install');
  console.error('   3. Check for any error messages above');
  process.exit(1);
}

