// Offline-capable customer creation mutation
export function useCreateCustomerOffline() {
  const { userId } = useAuth();
  const { isOnline } = useNetworkStatus();
  const queryClient = useQueryClient();

  return useMutation({
    mutationFn: async (customerData: any) => {
      if (!userId) throw new Error('User not authenticated');

      const newCustomer = {
        ...customerData,
        user_id: userId,
        id: crypto.randomUUID(),
        created_at: new Date().toISOString(),
        total_credit: 0,
      };

      console.log('ğŸ’¾ Creating customer - Online:', isOnline);

      if (isOnline) {
        try {
          // Try online creation first
          const result = await supabaseService.createCustomer(userId, customerData);

          // Store in offline storage too
          await offlineStorage.store('customers', result);

          return result;
        } catch (error) {
          console.log('âš ï¸ Online creation failed, storing offline');
          // Store offline and queue for sync
          await offlineStorage.store('customers', newCustomer);
          await offlineStorage.addPendingAction({
            id: crypto.randomUUID(),
            table: 'customers',
            action: 'create',
            data: newCustomer,
            timestamp: Date.now(),
            synced: false
          });

          return newCustomer;
        }
      } else {
        // Store offline and queue for sync
        console.log('ğŸ“± Storing customer offline for sync');
        await offlineStorage.store('customers', newCustomer);
        await offlineStorage.addPendingAction({
          id: crypto.randomUUID(),
          table: 'customers',
          action: 'create',
          data: newCustomer,
          timestamp: Date.now(),
          synced: false
        });

        return newCustomer;
      }
    },
    onSuccess: () => {
      // Invalidate and refetch customers
      queryClient.invalidateQueries({ queryKey: ['customers', userId, 'offline'] });
    },
  });
}

