    const { count, error } = await supabase.from('customers').select('count', { count: 'exact', head: true });
    if (error) {
      console.error('âŒ SUPABASE CONNECTION FAILED:', error);
    } else {
      console.log('âœ… SUPABASE CONNECTION SUCCESS: Total customers =', count);
    }
  } catch (err) {
