// Current user ID - dynamically fetched from auth
export const getCurrentUserId = async (): Promise<string | null> => {
  const { data: { user } } = await supabase.auth.getUser();
  return user?.id || null;
};

// Get current user ID synchronously (for components that need immediate access)
export const getCurrentUserIdSync = (): string | null => {
  // This will only work if session is already loaded
  const session = supabase.auth.getSession();
  return session ? (session as any).data?.session?.user?.id || null : null;
};

// Legacy demo user ID (only used for fallback when no auth)
export const DEMO_USER_ID = '11111111-1111-1111-1111-111111111111';

// Database service functions - NO OFFLINE FALLBACKS, ONLY REAL DATA
export const supabaseService = {
  // Users
  async getUser(id: string): Promise<User | null> {
    const { data, error } = await supabase
      .from('users')
      .select('*')
      .eq('id', id)
      .single();

    if (error) throw error;
    return data;
  },

  async getUserByUsername(username: string): Promise<User | null> {
    const { data, error } = await supabase
      .from('users')
      .select('*')
      .eq('username', username)
      .single();

    if (error && error.code !== 'PGRST116') throw error;
    return data;
  },

  async createUser(user: InsertUser): Promise<User> {
    const { data, error } = await supabase
      .from('users')
      .insert(user)
      .select()
      .single();

    if (error) throw error;
    return data;
  },

  // Customers - REAL SUPABASE DATA WITH SMART CACHING
  async getCustomers(userId: string): Promise<Customer[]> {
    console.log('ğŸ”¥ FETCHING CUSTOMERS for user:', userId);

    // Try cache first
    const { cacheManager, createCacheKey } = await import('./cache-manager');
    const cacheKey = createCacheKey('customers', userId);
    const cached = cacheManager.get<Customer[]>(cacheKey);

    if (cached) {
      console.log('ğŸ“¦ CACHE: Using cached customers data');
      return cached;
    }

    const { data, error } = await supabase
      .from('customers')
      .select('*')
      .eq('user_id', userId)
      .order('created_at', { ascending: false });

    if (error) {
      console.error('âŒ Error fetching customers:', error);
      throw error; // NEVER fall back to offline data
    }

    // Cache the results for 3 minutes
    cacheManager.set(cacheKey, data || [], 3 * 60 * 1000);

    console.log('âœ… Customers fetched from Supabase:', data?.length || 0, data);
    return data || [];
  },

  async createCustomer(userId: string, customer: InsertCustomer): Promise<Customer> {
    console.log('ğŸ”¥ Creating customer:', customer);

    // First ensure the user profile exists
    try {
      const { data: currentUser } = await supabase.auth.getUser();
      if (currentUser.user && currentUser.user.id === userId) {
        // Get user metadata from auth
        const userMetadata = currentUser.user.user_metadata || {};
        const businessName = userMetadata.businessName || userMetadata.business_name || 'à¦¦à§‹à¦•à¦¾à¦¨';
        const ownerName = userMetadata.name || userMetadata.owner_name || 'Owner';

        // Try to create or update user profile with correct column names
        const { error: profileError } = await supabase
          .from('users')
          .upsert({
            id: userId,
            username: currentUser.user.email?.split('@')[0] || 'user',
            password: 'supabase_managed', // Password managed by Supabase Auth
            shop_name: businessName,
            owner_name: ownerName,
            business_name: businessName,
            email: currentUser.user.email || '',
            phone_number: userMetadata.phone || '',
            address: userMetadata.address || '',
            created_at: new Date().toISOString()
          }, { 
            onConflict: 'id' 
          });

        if (profileError) {
          console.warn('âš ï¸ Profile upsert warning:', profileError);
        } else {
          console.log('âœ… User profile ensured for:', userId);
        }
      }
    } catch (profileErr) {
      console.warn('âš ï¸ Profile check warning:', profileErr);
    }

    const dbCustomer = {
      name: customer.name,
      phone_number: customer.phone_number || '',
      address: customer.address || '',
      user_id: userId,
      total_credit: customer.total_credit || 0
    };

    console.log('ğŸ”§ Database customer object:', dbCustomer);

    const { data, error } = await supabase
      .from('customers')
      .insert(dbCustomer)
      .select()
      .single();

    if (error) {
      console.error('âŒ Error creating customer:', error);
      console.error('âŒ Error details:', JSON.stringify(error, null, 2));
      throw new Error(`Customer creation failed: ${error.message}`);
    }

    console.log('âœ… Customer created successfully:', data);
    return data;
  },

  async getCustomer(userId: string, customerId: string): Promise<Customer | null> {
    console.log('ğŸ”¥ FETCHING CUSTOMER:', customerId, 'for user:', userId);

    const { data, error } = await supabase
      .from('customers')
      .select('*')
      .eq('id', customerId)
      .eq('user_id', userId)
      .single();

    if (error) {
      // Handle "no rows" error gracefully
      if (error.code === 'PGRST116') {
        console.log('âŒ Customer not found:', customerId);
        return null;
      }
      console.error('âŒ Error fetching customer:', error);
      throw error;
    }

    console.log('âœ… Customer fetched:', data);
    return data;
  },

  async updateCustomer(id: string, updates: Partial<InsertCustomer>): Promise<Customer> {
    const { data, error } = await supabase
      .from('customers')
      .update(updates)
      .eq('id', id)
      .select()
      .single();

    if (error) throw error;
    return data;
  },

  async deleteCustomer(id: string): Promise<void> {
    const { error } = await supabase
      .from('customers')
      .delete()
      .eq('id', id);

    if (error) {
      console.error('âŒ Error deleting customer:', error);
      throw error;
    }
  },

