        )}
        onClick={handleStatusClick}
        data-testid="status-network"
      >
        {isOnline ? (
          <>
            <Wifi size={12} />
            à¦…à¦¨à¦²à¦¾à¦‡à¦¨
          </>
        ) : (
          <>
            <WifiOff size={12} />
            à¦…à¦«à¦²à¦¾à¦‡à¦¨
          </>
        )}
      </Badge>

      {/* Sync Status */}
      {pendingCount > 0 && (
        <Badge
          variant="secondary"
          className={cn(
            "text-xs font-medium",
            issyncing && "animate-pulse"
          )}
          data-testid="status-sync"
        >
          {issyncing ? (
            <>
              <RefreshCw size={12} className="animate-spin mr-1" />
              à¦¸à¦¿à¦™à§à¦• à¦¹à¦šà§à¦›à§‡
            </>
          ) : (
            <>
              <AlertCircle size={12} className="mr-1" />
              {pendingCount} à¦…à¦ªà§‡à¦•à§à¦·à¦®à¦¾à¦£
            </>
          )}
        </Badge>
      )}

      {/* Sync Button */}
      {showSyncButton && isOnline && !issyncing && (
        <Button
          size="sm"
          variant="outline"
          onClick={handleSyncClick}
          className="h-6 px-2 text-xs"
          data-testid="button-force-sync"
        >
          <RefreshCw size={12} className="mr-1" />
          à¦¸à¦¿à¦™à§à¦•
        </Button>
      )}

      {/* Detailed Status Panel */}
      {showDetails && (
        <div className="absolute top-full left-0 mt-2 p-3 bg-white dark:bg-gray-800 border rounded-lg shadow-lg z-50 min-w-[200px]">
          <div className="space-y-2 text-xs">
            <div className="flex items-center justify-between">
              <span className="text-muted-foreground">à¦¨à§‡à¦Ÿà¦“à¦¯à¦¼à¦¾à¦°à§à¦•:</span>
              <div className="flex items-center gap-1">
                {isOnline ? (
                  <>
                    <CheckCircle size={12} className="text-green-600" />
