                  </>
                ) : (
                  <>
                    <AlertCircle size={12} className="text-red-600" />
                    <span className="text-red-600">à¦¬à¦¿à¦šà§à¦›à¦¿à¦¨à§à¦¨</span>
                  </>
                )}
              </div>
            </div>

            <div className="flex items-center justify-between">
              <span className="text-muted-foreground">à¦¶à§‡à¦· à¦…à¦¨à¦²à¦¾à¦‡à¦¨:</span>
              <span>{formatTime(lastOnlineTime)}</span>
            </div>

            {pendingCount > 0 && (
              <div className="flex items-center justify-between">
                <span className="text-muted-foreground">à¦…à¦ªà§‡à¦•à§à¦·à¦®à¦¾à¦£:</span>
